body.script:
//
const Antinna = {
  init() {
    this.fetchMentors();
    this.initTerminal();
    this.runTicker();
    this.updateClock();
    setInterval(() => this.updateClock(), 1000);
  },

  updateClock() {
    const el = document.getElementById('system-clock');
    const now = new Date();
    if (el) el.innerText = now.getHours().toString().padStart(2, '0') + ':' + 
                         now.getMinutes().toString().padStart(2, '0') + ':' + 
                         now.getSeconds().toString().padStart(2, '0') + ' RELAY_SYNC';
  },

  async fetchMentors() {
    try {
      const res = await fetch('/feeds/posts/default/-/Mentor?alt=json&max-results=10');
      const data = await res.json();
      const feed = document.getElementById('mentor-feed');

      if(data.feed.entry) {
        feed.innerHTML = '';
        data.feed.entry.forEach(entry => {
          const name = entry.title.$t.toUpperCase();
          const img = entry.media$thumbnail ? entry.media$thumbnail.url.replace('s72-c', 's150-c') : 'https://via.placeholder.com/150/111/00d4ff?text=M';
          const link = entry.link.find(l => l.rel === 'alternate').href;

          feed.innerHTML += `
            <div class="mentor-node" onclick="location.href='${link}'">
              <div class="mentor-avatar-wrap">
                <img src="${img}" alt="${name}"/>
                <div class="status-pulse"></div>
              </div>
              <div style="font-size:8px; margin-top:8px; color:var(--accent-cyan); font-family:'JetBrains Mono'; font-weight:700; letter-spacing:1px;">${name.split(' ')[0]}</div>
            </div>
          `;
        });
      }
    } catch(e) { console.warn("Antinna: Relay node unreachable."); }
  },

  initTerminal() {
    const trigger = document.getElementById('dock-trigger');
    const overlay = document.getElementById('terminal-ui');
    const close = document.getElementById('close-term');

    trigger.onclick = () => {
      overlay.style.display = 'flex';
      this.typeWriter("term-typing", "> INITIALIZING NEURAL UPLINK... [SUCCESS] > WAITING FOR COMMAND_");
    };
    close.onclick = () => overlay.style.display = 'none';
  },

  typeWriter(id, text) {
    const el = document.getElementById(id);
    el.innerText = '';
    let i = 0;
    const interval = setInterval(() => {
      el.innerText += text[i];
      i++;
      if(i === text.length) clearInterval(interval);
    }, 20);
  },

  termCmd(cmd) {
    this.typeWriter("term-typing", `> EXECUTING ${cmd}... ENCRYPTING SIGNAL... DISPATCHED.`);
    setTimeout(() => document.getElementById('terminal-ui').style.display = 'none', 1500);
  },

  runTicker() {
    const ticker = document.getElementById('price-ticker');
    const materials = [
      "CEMENT_U1: $482.50 [STABLE]", 
      "REBAR_H2: $914.20 [RISING]", 
      "GRAVEL_L1: $44.10 [FALLING]", 
      "BITUMEN_X: $122.90 [VOLATILE]"
    ];
    let idx = 0;
    setInterval(() => {
      ticker.style.opacity = 0;
      setTimeout(() => {
        ticker.innerText = materials[idx];
        ticker.style.opacity = 1;
        idx = (idx + 1) % materials.length;
      }, 300);
    }, 5000);
  }
};

document.addEventListener('DOMContentLoaded', () => Antinna.init());
