body.script:
//
const Antinna = {
  init() {
    this.startTime();
    this.loadUplinks();
    this.initTerminal();
    this.runMarketTicker();
  },

  startTime() {
    setInterval(() => {
      const now = new Date();
      document.getElementById('syst-time').innerText = 
        now.toLocaleTimeString('en-GB', { hour12: false }) + ' L_UTC';
    }, 1000);
  },

  async loadUplinks() {
    const stream = document.getElementById('mentor-stream');
    try {
      const res = await fetch('/feeds/posts/default/-/Mentor?alt=json&max-results=12');
      const data = await res.json();
      if (data.feed.entry) {
        stream.innerHTML = '';
        data.feed.entry.forEach(e => {
          const name = e.title.$t.toUpperCase();
          const thumb = e.media$thumbnail ? e.media$thumbnail.url.replace('s72-c', 's200-c') : 'https://via.placeholder.com/200/111/00f2ff?text=NODE';
          const link = e.link.find(l => l.rel === 'alternate').href;
          stream.innerHTML += `
            <div class="uplink-node" onclick="location.href='${link}'">
              <div class="node-frame"><img src="${thumb}"/></div>
              <div style="font-family:var(--font-mono); font-size:8px; color:var(--accent-cyan); text-align:center; margin-top:8px;">${name.split(' ')[0]}</div>
              <div class="node-status"></div>
            </div>
          `;
        });
      }
    } catch (err) {
      console.warn("Uplink failed. Node offline.");
    }
  },

  initTerminal() {
    const shell = document.getElementById('terminal-shell');
    const trigger = document.getElementById('term-trigger');
    const input = document.getElementById('term-input');

    trigger.onclick = () => { shell.style.display = 'flex'; input.focus(); };
    document.getElementById('close-term').onclick = () => shell.style.display = 'none';

    input.onkeydown = (e) => {
      if (e.key === 'Enter') {
        const val = input.value.toUpperCase();
        this.log(`> USER_EXEC: ${val}`);
        this.exec(val);
        input.value = '';
      }
    };
  },

  log(msg) {
    const out = document.getElementById('term-output');
    const div = document.createElement('div');
    div.innerText = msg;
    out.appendChild(div);
    out.scrollTop = out.scrollHeight;
  },

  exec(cmd) {
    switch(cmd) {
      case 'CLEAR': document.getElementById('term-output').innerHTML = ''; break;
      case 'RESCAN': 
        this.log("> INITIATING SYSTEM SCAN...");
        setTimeout(() => this.log("> SCAN COMPLETE. 0 THREATS DETECTED."), 1000);
        break;
      case 'UPLINK':
        this.log("> RESETTING NEURAL NODES...");
        this.loadUplinks();
        break;
      case 'LOGS':
        this.log("> ACCESSING SECURE LOG_BUFFER...");
        this.log("> [INFO] KERNEL VERSION 1.5.1-PATCHED");
        this.log("> [WARN] LATENCY SPIKE IN SECTOR 4");
        break;
      default:
        this.log(`> ERROR: COMMAND '${cmd}' NOT RECOGNIZED.`);
    }
  },

  runMarketTicker() {
    const content = document.getElementById('ticker-content');
    const data = [
      "CONCRETE_RAW: $540.2 (+1.2%)",
      "STEEL_BEAM_X: $1202.5 (-0.4%)",
      "BIO_POLYMER: $88.1 (STABLE)",
      "POWER_GRID_L: $0.12/kW (PEAK)"
    ];
    let i = 0;
    setInterval(() => {
      content.style.opacity = 0;
      setTimeout(() => {
        content.innerText = data[i];
        content.style.opacity = 1;
        i = (i + 1) % data.length;
      }, 400);
    }, 4000);
  }
};

document.addEventListener('DOMContentLoaded', () => Antinna.init());
